parameters.functionCode:
const alertas = $input.first().json;
const alertasCriticas = [];
const alertasImportantes = [];
const alertasNormales = [];

// Clasificar alertas por gravedad
alertas.forEach(alerta => {
  if (alerta.nivel_gravedad === 'CRITICO') {
    alertasCriticas.push(alerta);
  } else if (alerta.nivel_gravedad === 'ALTO') {
    alertasImportantes.push(alerta);
  } else {
    alertasNormales.push(alerta);
  }
});

return {
  total: alertas.length,
  criticas: alertasCriticas.length,
  importantes: alertasImportantes.length,
  normales: alertasNormales.length,
  detalles: {
    criticas: alertasCriticas,
    importantes: alertasImportantes,
    normales: alertasNormales
  }
};

